#include <stdio.h>
#include <string.h>
#include <stdlib.h>

void reverse(char *alph, int n) {

  int i;
  char temp = 0;

  for(i = 0; i <= n/2; i++) {
    temp = alph[i];
    alph[i] = alph[n - 1 - i];
    alph[n - 1 - i] = temp;
  }
}

char *ReverseComplement(char *alphabet) {

  int i = 0;

  for(i = 0; alphabet[i] != 0; ++i) {
  
    if(alphabet[i] == 'A') {
      alphabet[i] = 'T';
    }
    else if(alphabet[i] == 'C'){
      alphabet[i] = 'G';      
    }
    else if(alphabet[i] == 'G') { 
      alphabet[i] = 'C';      
    }
    else if(alphabet[i] == 'T') {
      alphabet[i] = 'A';      
    } 

  } 

  reverse(alphabet, i);
  return alphabet;

}


int main() {
  int i;

  char m[] = {""};  
  
  FILE *fp;
  size_t count;

  char const *str = ReverseComplement(m);
  
  fp = fopen("1.txt", "wb");
  if(fp == NULL) {
      perror("ошибка открытия файла");
      return 0;
  }
  count = fwrite(m, strlen(str), 1, fp);
  fclose(fp);

 return 0;
}